package com.class03;

public class IfElseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		 int month = 1;
	        if (month == 1) {
	            System.out.println("jan");
	        }
	        else if ( month == 2) {
	            System.out.println("feb");
	        }
	        else if ( month == 3) {
	            System.out.println("marc");
	        }
	        else {
	            System.out.println("invalid month");
	        }
		
		}
				
	}


