public class LoopReview {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//i want to create a multiplication table
		
		
		int num=2;
		int mult;
		for(int i=1; i<=10; i++) {
			 mult = num*i;
			System.out.println(num+"*"+i+"="+mult);
		}
		
		
	}

}
